package com.syntax.week1HW;

public class ArithmeticOperations010 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*  Declare 2 numbers and assign values 200 and 100 respectively.
 * 2. First, add numbers, then subtract, third multiply, forth divide. 
 * 3. Please use variables to print the values of each operation result on a separate line.
 */
		int var2=100;
		int var1=200;
		
		int first=var1+var2;
		int second=var1-var2;
		int third=var1*var2;
		int fourth=var1/var2;
		
		System.out.println(first);
		System.out.println(second);
		System.out.println(third);
		System.out.println(fourth);
		
	}

}
