package com.syntax.week1HW;

public class ArithmeticOperationsModulus017 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1, num2, sub, mult, div, mod;
		num1=-21;
		num2=4;
		div=num1/num2;
		mult=div*num2;
		sub=num1-mult;
		mod=sub;
		
		System.out.println(mod);

	}

}
